$(function () {
    var $popover = $('#popover')
        .css('display', 'block')
        .css('padding', '0')
        .css('position', 'relative');
    var $img = $popover.contents().filter(function () {
        return 'IMG' == this.nodeName;
    });
    var $map = $popover.contents().filter(function () {
        return 'MAP' == this.nodeName;
    });
    var $area = $map.children('area').each(function() {
        let coord = this.coords.split(',')
        let coords = []
        for (var i = 0;i < coord.length;i++) {
            coords.push(Number.parseInt(coord[i]));
        }
        let setting = _popover[this.id] || _popover['']
        if (coords.length >= 4) {
            var $mask = $(`<div class="popover-mask"  data-toggle="popover" data-placement="top" title="" data-trigger="hover" template="" ></div>`)
                .css('left', `${coords[0]}px`)
                .css('top', `${coords[1]}px`)
                .css('width', `${coords[2] - coords[0]}px`)
                .css('height', `${coords[3] - coords[1]}px`)
                .appendTo($popover);
            $mask.popover(setting)
			
        }
    })
})